INSERT INTO tratamientos_medicos (codigo_tratamiento, id_tipo_tratamiento, nombre_tratamiento, descripcion_tratamiento, costo_tratamiento) VALUES
	('CG1E1', 1, 'Extirpación de tumor en la cabeza', 'Extracción del tumor, reconstrucción del defecto.', 10000.00),
	('CG1C1', 1, 'Colecistectomía abierta', 'La vesicula biliar es extirpada a través de una incisión en el costado derecho de la caja toráxica.', 1200.00),
	('DT2D1', 2, 'Dieta controlada en lípidos', 'Restricción del consumo de hidratos de carbono.', 45.00),
	('DT2D2', 2, 'Dieta controlada en proteínas', 'Normocalórica. Cubrir aumentando lo necesario HC y grasas', 45.00),
	('FM3TR', 3, 'Terapia restaurativa', 'No guarda relación con la enfermedad original y se emplea frecuentemente durante el periodo de convalecencia.', 70.65),
	('FM3TC', 3, 'Terapia curativa', 'El tratamiento se drige a la erradicación de uno o más de los agentes etiológicos o causantes de la enfermedad', 95.00),
	('FT4O1', 4, 'Osteopatía', 'Tratar las disfunciones, la hipermovilidad o hipomovilidad que se puedan encontrar en la columna vertebral', 50.00),
	('FT4N1', 4, 'Neurodinamia', 'Tratamiento de los nervios que recorren el cuerpo como si fueran cables, atravesando numerosas estructuras donde pueden quedar atrapados generando una sintomatología típica de dolor neuropático', 48.00),
	('HT5E1', 5, 'Esguinces', 'Ejercicios en agua mejoran la recuperación del esguince del tobillo y reducen el tiempo de retorno a las competiciones.', 60.00),
	('HT5C1', 5, 'Ciática', 'Con este tratamiento se promueve la disminución de la rigidez muscular, el dolor,  el aumento de la circulación, movimiento articular, fortalecimiento de músculos débiles y se produce una sensación de bienestar general.', 75.00),
	('LP6A1', 6, 'Afasia', 'Terapia del habla y del lenguaje intenta mejorar la capacidad de comunicación al recuperar la mayor parte del lenguaje posible.', 65.00),
	('LP6D1', 6, 'Disfagia', 'Ejercicios de aprendizaje, mostrar diferentes maneras de colocar los alimentos en la boca y posicionar el cuello y la cabeza para que sea fácil tragar', 45.00),
	('OT7F1', 7, 'Fracturas', 'Reducción, que debe completa, o suficiente para que exista una correcta alineación de los fragmentos, inmovilización para permitir la consolidación, recuperación de la función', 100.00),
	('OT7A1', 7, 'Artritis', 'Férulas de reposo para mano, soportes de muñeca, férulas para dedos y calzado y prótesis de calzado especiales.', 150.00),
	('PT8P1', 8, 'Prótesis removible', 'Procedimiento clínico dirigido a restituir dientes ausentes mediante unos aparatos o productos sanitarios a medida, no fijos', 80.00),
	('PT8P2', 8, 'Prótesis fija', 'Procedimiento clínico dirigido a restituir dientes ausentes mediante una aparatología fija, unida permanentemente a algún/-os dientes naturales', 150.00),
	('PS9H1', 9, 'Hipnosis', 'Tratamiento para el insomnio, la enuresis nocturna, el tabaquismo y el consumo excesivo de alimentos.', 55.00),
	('PS9L1', 9, 'Logoterapía', 'Tiene como objetivo la búsqueda del sentido de la vida de la propia persona.', 45.50),
	('QT10Q1', 10, 'Quimioterapia', 'Tratamiento del cáncer que usa fármacos para destruir células cancerosas.', 65.00),
	('RT11C1', 11, 'Cáncer', 'Tratamiento que usa altas dosis de radiación para destruir células cancerosas y reducir tumores.', 125.00),
	('RH12O1', 12, 'Rehabilitación ortópedica', 'Optimizar el proceso de curación de la lesión, disminuir el dolor, restaurar la movilidad.', 45.50),
	('RH12P1', 12, 'Rehabilitación pulmonar', 'El trtamiento consiste en realizar técnicas de respiración, técnicas para evitar cansarse, educación sobre cómo controlar la enfermedad.', 60.00),
	('RP13R1', 13, 'Reposo Laboral', 'El reposo laboral es una herramienta terapéutica que se emplea ocasionalmente como parte inicial de un tratamiento médico', 15.00),
	('ST14S1', 14, 'Sueroterapia Biológica', 'Tratamiento consistente en la administración de sueros específicos que favorecen la autocuración del organismo a partir de una selección de vitaminas', 73.00),
	('TQ15C1', 15, 'Cardiopatías', 'Se administra ácido etilendiaminotetraacético disódico a través de una serie de tratamientos intravenosos semanales, cada uno de los cuales dura aproximadamente 30 minutos.', 60.00),
	('TQ15A1', 15, 'Accidentes cerebrovasculares', 'Se administra ácido etilendiaminotetraacético disódico a través de una serie de tratamientos intravenosos semanales, cada uno de los cuales dura aproximadamente 30 minutos.', 60.00),
	('TO16O1', 16, 'Oncología', 'La terapia ocupacional en oncología ayuda a enfrentar las secuelas físicas y cognitivas que deja la enfermedad, además de los efectos emocionales por la pérdida de rutinas y hábitos', 200.00),
	('TO16G1', 16, 'Geriatría', 'La terapia ocupacional se enfoca en tratamientos donde el paciente geriátrico se involucra activamente en las actividades que le parecen significativas para su bienestar y en las cuales puede tener independencia', 175.00);
INSERT INTO medicamentos  (codigo_medicamento, id_tipo_medicamento, nombre_medicamento, descripcion_medicamento, via_administracion, presentacion_medicamento, costo_medicamento, existencia_medicamento) VALUES
	('A1A1', 1, 'Acetaminofén', 'Analgésico y antipirético. Bloquea la generación del impulso doloroso a nivel periférico.', 'Vía oral', 'Tabletas', 2.67, 'En existencia'),
	('A1A2', 1, 'Aspirina', 'Reduce el dolor, la fiebre, la inflamación y la coagulación de la sangre. Tipo de antiinflamatorio no esteroide.', 'Vía oral', 'Tabletas', 5.62, 'En existencia'),
	('A1C1', 1, 'Codeína', 'Alcaloide que se encuentra de forma natural en el opio. Se utiliza con fines médicos como analgésico, sedante y antitusígeno.', 'Vía oral', 'Tabletas', 17.46, 'Agotado'),
	('A1M1', 1, 'Morfina', 'Sustancia controlada, opiácea agonista utilizada en premedicación, anestesia, analgesia, tratamiento del dolor asociado a la isquemia miocárdica', 'Vía intramuscular.', 'Ampolla', 42.78, 'En existencia'),
	('A2A1', 2, 'Amoxicilina', 'Penicilina semisintética, sensible a la penicilinasa de amplio espectro, es bactericida y actúa inhibiendo la biosíntesis del mucopéptido de la pared celular bacteriana.', 'Vía intravenosa', 'Ampolla', 5.81, 'Agotado'),
	('A2P1', 2, 'Penicilina', 'Antibiótico del grupo de los betalactámicos empleados profusamente en el tratamiento de infecciones provocadas por bacterias sensibles.', 'Vía intramuscular', 'Ampolla', 6.45, 'En existencia'),
	('A2T1', 2, 'Tetraciclina', 'Se usa para tratar las infecciones causadas por bacterias incluidas la neumonía y otras infecciones del tracto respiratorio', 'Vía oral', 'Tabletas', 9.41, 'Agotado'),	
	('V3A1', 3, 'Antidiftérica', 'Productor de toxina e inactivado con formol (asociado siempre con toxoide tetánico', 'Vía intramuscular', 'Ampolla', 17.38, 'En existencia'),
	('V3A2', 3, 'Antitetánica', 'Vacuna inactivada y contiene la toxina que fabrica la bacteria, pero desprovista totalmente de su toxicidad', 'Vía intramuscular', 'Ampolla', 25.72, 'En existencia'),
	('V3A3', 3, 'Antipoliomielítica', 'Vacuna preventiva que induce inmunidad activa y específica contra la Poliomielitis.', 'Vía subcutánea', 'Ampolla', 15.84, 'Agotado'),
	('A4T1', 4, 'Tintura de yodo', 'De uso general en pequeñas heridas o superficiales y en la desinfección de la piel antes de punción venosa', 'Vía tópica', 'Solución', 5.30, 'En existencia'),
	('A4A1', 4, 'Agua oxigenada', 'Solución de peróxido de hidrógeno en agua. Este compuesto es bastante oxidante y capaz de destruir componentes de las células.', 'Vía tópica', 'Solución', 6.77, 'En existencia'),
	('A4A2', 4, 'Alcohol etílico', 'Es un líquido transparente con olor característico. Es una materia prima importante para síntesis.', 'Vía tópica', 'Solución', 3.0, 'Agotado'),
	('A5D1', 5, 'Diclofenac de sodio', 'Se usa para tratar los síntomas de la artritis reumatoide. Bloquea las sustancias que causan inflamación y dolor.', 'Vía oral', 'Tabletas', 1.59, 'En existencia'),
	('A5I1', 5, 'Ibuprofeno', 'Se usa para tratar la fiebre, la hinchazón, el dolor y el enrojecimiento al impedir que el cuerpo elabore sustancias que causan inflamación.', 'Vía oral', 'Tabletas', 2.00, 'En existencia'),
	('A5M1', 5, 'Meloxicam', 'Funciona al detener la producción del cuerpo de una sustancia que causa dolor, fiebree inflamación.', 'Vía oral', 'Tabletas', 2.54, 'Agotado'),
	('A6C1', 6, 'Clorfeniramina', 'Se usa para aliviar el enrojecimiento, la picazón y el lagrimeo de ojos; los estornudos; la irritación de nariz o garganta; la secreción nasal ocasionados por las alergias y el resfriado común.', 'Vía oral', 'Tabletas', 3.88, 'En existencia'),
	('A6C2', 6, 'Cetirizina', 'Fármaco utilizado para el tratamiento de la alergia. Es un antihistamínico considerado de segunda generación por no provocar somnolencia.', 'Vía oral', 'Solución', 3.12, 'Agotado'),
